import React, { useRef, useLayoutEffect, useState } from 'react';
import logo from './logo.svg';
import * as am4core from "@amcharts/amcharts4/core";
import * as am4charts from "@amcharts/amcharts4/charts";
import am4themes_animated from "@amcharts/amcharts4/themes/animated";
import styled from 'styled-components';
import {space, color, layout} from 'styled-system';
import Card from 'react-bootstrap/Card';
import axios from 'axios'

var user_id = "634739719";

const TrendTable = styled.table`
&&& {
  font-family: arial, sans-serif;
  border-collapse: collapse;
  width: 100%;
}`

const TrendTd = styled.td`{
  text-align: left;
  padding: 8px;
}`

const TrendNum = styled.td`{
  text-align: right;
  padding: 8px;
  width: 25%;
}`

const TrendTrGray = styled.tr `{
  background-color: #dddddd;
}`

am4core.useTheme(am4themes_animated);

var rd_grad = new am4core.LinearGradient();
rd_grad.addColor(am4core.color("#9EFA99"));
rd_grad.addColor(am4core.color("#7BD2FA"));

var rs_grad = new am4core.LinearGradient();
rs_grad.addColor(am4core.color("#DB6EF0"));
rs_grad.addColor(am4core.color("#9EFA99"));

var ds_grad = new am4core.LinearGradient();
ds_grad.addColor(am4core.color("#7BD2FA"));
ds_grad.addColor(am4core.color("#DB6EF0"));

var rds_grad = new am4core.LinearGradient();
rds_grad.addColor(am4core.color("#9EFA99"));
rds_grad.addColor(am4core.color("#7BD2FA"));
rds_grad.addColor(am4core.color("#DB6EF0"));

var series_dict = {
  r: {col: am4core.color( "#9EFA99" ),
        name: "Researchers"
       },
  d: {col: am4core.color( "#7BD2FA" ),
       name: "Designers"
      },
  s: {col: am4core.color( "#DB6EF0" ),
       name: "Software Engineers"
      },
  o: {col: am4core.color( "#FA9810" ),
  name: "Other Publics"
 },
  rd: {col: rd_grad,
  name: "Researcher/Designers"
 },
 rs: {col: rs_grad,
  name: "Researcher/Engineers"
 },
 ds: {col: ds_grad,
  name: "Designer/Engineers"
 },
 rds: {col: rds_grad,
  name: "Researcher/Designer/Engineers"
 },
}

function compare( a, b ) {
  if ( a.value < b.value ){
    return -1;
  }
  if ( a.value > b.value ){
    return 1;
  }
  return 0;
}

function organizeData(data, num, active){
  var new_data = []
  /*
  var num_coms = 0
  for (const key in active){
    if (active[key]){
      num_coms++
    }
  }
  if (num_coms > 0){
    num = Math.round(num / num_coms)
  }
  */
  
  for (const key in data){
    if (!active[key]){
      continue
    }
    //console.log("Organizing")
    //var i = 0
    for (const n in data[key]){
      //i = i + 1
      //if (i >= Object.keys(data[key]).length - num){
      new_data.push({"name": String(n),
                     "value": data[key][n]["num"],
                    "color": series_dict[key]['col'],
                    "sample": data[key][n]["tweets"]})
     // }
      
    }
  }
  new_data.sort(compare)
  if (new_data.length > 10){
    new_data = new_data.slice(-10)
  }
  return (new_data)
}

function renderTrendRow(data, gray){
  console.log(data)
  if (gray){
    return (
      <TrendTrGray>
    <TrendTd>{data.name}</TrendTd>
    <TrendNum>{data.value} tweets</TrendNum>
  </TrendTrGray>
    )
  } else {
  return(
    <tr>
    <TrendTd>{data.name}</TrendTd>
    <TrendNum>{data.value} tweets </TrendNum>
  </tr>
  )
  }
}

function renderEmptyRow(gray){
  if (gray){
    return (
      <TrendTrGray>
    <TrendTd>_____</TrendTd>
    <TrendNum>___</TrendNum>
  </TrendTrGray>
    )
  } else {
  return(
    <tr>
    <TrendTd>_____</TrendTd>
    <TrendNum>___</TrendNum>
  </tr>
  )
  }
}

function renderTrendTable(data){
  const rows =[]
  var gray = true
  var empties = 0
  for (var i = 9; i >= 0; i--){
    if (data.length <= i){
      empties++;
    } else{
      gray = !gray
      rows.push(renderTrendRow(data[i], gray))
    }
  }
  for (var i = 0; i < empties; i++){
    gray = !gray
    rows.push(renderEmptyRow(gray))
  }
  return(
    <TrendTable>
      {rows}
    </TrendTable>
  )

}

function Trending(props) {
  var data = []
  const [activeComs, setActiveComs] = useState({"r": true, "d": true, "s": true, "o": true})
  data = organizeData(props.data, 10, activeComs)
  const[table, setTable] = useState(renderTrendTable(data))

  const[comList, setComList] = useState(<p>Topics among <strong>Researchers</strong>, <strong>Designers</strong>, <strong>Software Engineers</strong>, <strong>Others</strong> in your audience this week.</p>)

  const setState = (key) => {
    var state = {"r": activeComs["r"], "d": activeComs["d"], "s": activeComs["s"], "o": activeComs["o"]}
    state[key] = !state[key]
    setActiveComs(state)
    var p = writeComList(activeComs)
    setComList(p)
  }

  function writeComList(coms){
    var r = <></>
    var d = <></>
    var s = <></>
    var o = <></>
    var num = 0

    if (activeComs.r){
      r = <strong> Researchers</strong>
      num++
    }
    if (activeComs.d){
      if (num > 0){d = <>,<strong> Designers</strong></>}
      else{d = <strong> Designers</strong>}
      num++
    }
    if (activeComs.s){
      if (num > 0){s = <>,<strong> Software Engineers</strong></>}
      else{d = <strong> Software Engineers</strong>}
      num++
    }
    if (activeComs.o){
      if (num > 0){o = <>,<strong> Others</strong></>}
      else{d = <strong>Others</strong>}
    }

    return <p> Trending topics among{r}{d}{s}{o} in your audience this week.</p>
  }

  useLayoutEffect(() => {
    data = organizeData(props.data, 10, activeComs)
    setTable(renderTrendTable(data))

  }, [activeComs]);

  return (
    <div>
        <div id={props.divid} style={{ width: "100%", height: "600px" }}>
          {comList}
{table}
<br/>
<form >
         <input type = "checkbox" name = "check_f_researchers" defaultChecked = {true} onClick={() => setState("r")}/> Researchers
         <br/>
         <input type = "checkbox" name = "check_f_designers" defaultChecked = {true} onClick={() => setState("d")}/> Designers
         <br/>
         <input type = "checkbox" name = "check_f_software" defaultChecked = {true} onClick={() => setState("s")}/> Software Engineers
         <br/>
         <input type = "checkbox" name = "check_f_other" defaultChecked = {true} onClick={() => setState("o")}/> Other Publics
      </form>
        </div>
    </div>
    
  );
}
export default Trending;
