var yAxis = chart.yAxes.push(new am4charts.ValueAxis());

function createSeries(name, color) {
  let series = chart.series.push( new am4charts.LineSeries() );
  series.dataFields.categoryX = "col";
  series.dataFields.categoryY = "row";
  series.sequencedInterpolation = true;
  series.defaultState.transitionDuration = 3000;
  series.name = name;


  // Set up column appearance
  var bullet = series.bullets.push(new am4charts.CircleBullet());
  bullet.strokeOpacity = 0.2;
  bullet.stroke = am4core.color(color);
  bullet.nonScalingStroke = true;
  
  return series;
}

var s1 = createSeries("Researchers", "9efa99")
var s2 = createSeries("Other publics", "fa9810")

s1.data = []

// Legend
