import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import {space, color, layout} from 'styled-system';
import Card from 'react-bootstrap/Card';
import CardColumns from 'react-bootstrap/CardColumns';
import WaffleChart from './WaffleChart';
import axios from 'axios'
import { Tweet} from 'react-twitter-widgets'
import OverlayTrigger from 'react-bootstrap/OverlayTrigger'
import Popover from 'react-bootstrap/Popover'
import IconArray from './IconArray';

//var id = "634739719"
//var id = "1293416714923683841"
//var id = "192812670"
//var id = "802233409338675200"
//var id = "18256350"
//var id = "2490180804"
//var id = "18670940"
//var id = "486899842"
//var id = "238753895"

const Title = styled.p`
    &&& {
    padding: 1em;
    font-size: 30px;
    font-weight:bold;
}`

const Subtitle = styled.p`
    &&& {
    padding: .05em;
    font-size: 16px;
}`

const Retweet = styled.p`
    &&& {
    padding: 1em;
    font-size: 24px;
    font-weight:bold;
    margin-left:80px
}`

const tweetwind = {
    padding: '1em',
    width: '600px',
    maxWidth: '600px',
    maxHeight: '600px',
    'overflow-y': 'auto'
    
  };

const square = {
    width: "200px",
    maxWidth: "200px",
    maxHeight: "200x",
    height: "200px",
    padding: "0.5em",
}

const Pop = styled.button `{
    background:none;
    border:none;
    margin:0;
    padding:0;
    cursor: pointer;
    color:blue;
  }`
  
  const tweetpop = {
    width: '600px',
    maxWidth: 'none',
    maxHeight: '500px',
    'overflow-y': 'auto'
    
  };


/*
function RenderTweet(tweets, num){
    if (tweets.length < num){
        return (<div></div>);
    } else if (tweets[tweets.length - num]['downstream_r'] + tweets[tweets.length - num]['downstream_n'] <= 0){
        return (<div></div>);
    }
    else{
        return(
            <div>
                <Card style ={{padding:"3em"}}>
            <div className='columns'>
                <div className='column'>
                    <Tweet tweetId={tweets[tweets.length - num]['tweet_id_text']} options={{ width: "50%", cards: "hidden" }}/>

                    
                </div>
                <div className='column'>
                    <WaffleChart legend_pos = "right" title = {tweets[tweets.length - num]['retweets'].toString()} divid={"td".concat(num.toString())} vars = {[{name: "Researchers", data: tweets[tweets.length - num]['downstream_r']}, {name: "Non-researchers", data: tweets[tweets.length - num]['downstream_n']}]} />
                    <Retweet>
                    Retweets: {tweets[tweets.length - num]['retweets']}
                    </Retweet>
                </div>
            </div>
            </Card>

            <br/>
            </div>
        )
    }
}
*/

function PopButton(tweet_id, downstream, num_cols, num_rows) {
    return (
    <OverlayTrigger trigger="click" placement="left" overlay={TweetPop(tweet_id, downstream, num_cols, num_rows)}>
    <button>See Reach</button>
      
    </OverlayTrigger>
    )
  };
  
  function TweetPop(tweet_id, data, num_cols, num_rows){
    return(
    <div>
      <Popover id="popover-basic" placement="bottom" style={tweetpop}>
        <Popover.Title>Sample Tweets</Popover.Title>
        <Popover.Content>
            <IconArray divid = {tweet_id} data = {JSON.parse(data)} cols = {num_cols} rows = {num_rows}/>
        </Popover.Content>
      </Popover>
    </div>
    )
  }

function RenderTweet(tweet_id, downstream, num_cols, num_rows){



    return(
        <div key = {tweet_id}>
        <Tweet tweetId={tweet_id} options={{ width: "100%", cards: "hidden", conversation: "none"}}/>
        {PopButton(tweet_id, downstream, num_cols, num_rows)}
        </div>
    )


    /*
   var new_text = ""

   if (text.length > 110){
       new_text = text.slice(0, 110)
       new_text = new_text + "..."
   } else{
       new_text = text
   }

   return(
    <div key = {tweet_id}>
        <Card style = {square}>
        <p>{new_text}</p>
        </Card>
        <br/>
    </div>
   )
   */
   
}




function MyTweets(props) {
    const [isLoading, setLoading] = useState(true);
    const [tweets, setTweets] = useState([]);
    var id = props.id
  
    useEffect(() => {
        axios
        .get(`http://localhost:4001/twitter/retweetsWhereUserID/${id}`)
        .then(response => {
          setTweets(response.data);
          setLoading(false);
        })
        .catch(error => console.error(`There was an error retrieving the retweets list: ${error}`));
      }, []);

      if (isLoading) {
        return <div>

            <Title>My Tweets</Title>
            <p>Loading...</p>
 
        </div>
      }

      /*

      var col1 = []
      var col2 = []
      var col3 = []
      */
      var tweet_array = []

      var max = Math.min(tweets.length, 10)

      for (var i = 0; i < max; i++){
          /*
        if (i % 3 == 0){
            console.log("col1")
            col1.push(RenderTweet(tweets[i].tweet_id_text, tweets[i].tweet_text))
        }
        if (i % 3 == 1){
            console.log("col2")
            col2.push(RenderTweet(tweets[i].tweet_id_text, tweets[i].tweet_text))
        }
        if (i % 3 == 2){
            console.log("col3")
            col3.push(RenderTweet(tweets[i].tweet_id_text, tweets[i].tweet_text))
        }
        */
       tweet_array.push(RenderTweet(tweets[i].tweet_id_text, tweets[i].downstream_class, props.cols, props.rows))
      }

      return (
        <Card style = {tweetwind}>
            <div className='columns'>
                <div className='column'>
                    {tweet_array}
                </div>
            </div>
        
        
        </Card>
        );
    
    /*
    return (
<div>
    <Card style ={{padding:"3em"}}>
        <Title>My Tweets</Title>
            <div className='columns'>
                <div className='column'>
                    <Title>Tweets</Title>
                    <Tweet tweetId={tweets[tweets.length - 1]['tweet_id_text']} options={{ width: "50%", cards: "hidden" }}/>

                    
                </div>
                <div className='column'>
                    <Title>Downstream Audience</Title>
                    <WaffleChart title = {tweets[tweets.length - 1]['retweets']} divid="td1" r = {tweets[tweets.length - 1]['downstream_r']} n = {tweets[tweets.length - 1]['downstream_n']}/>
                </div>
            </div>

            <br/>
            <div className='columns'>
                <div className='column'>
                    <Tweet tweetId={tweets[tweets.length - 2]['tweet_id_text']} options={{ width: "50%", cards: "hidden" }}/>
                </div>
                <div className='column'>
                    <WaffleChart title = {tweets[tweets.length - 2]['retweets']} divid="td2" r = {tweets[tweets.length - 2]['downstream_r']} n = {tweets[tweets.length - 2]['downstream_n']}/>
                </div>
            </div>

            <br/>
            <div className='columns'>
                <div className='column'>
                    <Tweet tweetId={tweets[tweets.length - 3]['tweet_id_text']} options={{ width: "50%", cards: "hidden" }}/>
                </div>
                <div className='column'>
                    <WaffleChart title = {tweets[tweets.length - 3]['retweets']} divid="td3" r = {tweets[tweets.length - 3]['downstream_r']} n = {tweets[tweets.length - 3]['downstream_n']}/>
                </div>
            </div>

            <br/>
            <div className='columns'>
                <div className='column'>
                    <Tweet tweetId={tweets[tweets.length - 4]['tweet_id_text']} options={{ width: "50%", cards: "hidden" }}/>
                </div>
                <div className='column'>
                    <WaffleChart title = {tweets[tweets.length - 4]['retweets']} divid="td4" r = {tweets[tweets.length - 4]['downstream_r']} n = {tweets[tweets.length - 4]['downstream_n']}/>
                </div>
            </div>

            <br/>
            <div className='columns'>
                <div className='column'>
                    <Tweet tweetId={tweets[tweets.length - 5]['tweet_id_text']} options={{ width: "50%", cards: "hidden" }}/>
                </div>
                <div className='column'>
                    <WaffleChart title = {tweets[tweets.length - 5]['retweets']} divid="td5" r = {tweets[tweets.length - 5]['downstream_r']} n = {tweets[tweets.length - 5]['downstream_n']}/>
                </div>
            </div>

    </Card>

</div>
);*/
}


export default MyTweets;
