function CalculateData(nums: number[], num_cols, denom) {
  var data_list = []

  var cur_col = 1
  var cur_row = 1

  for (var i = 0; i < nums.length; i++){
    var m = Math.round(nums[i] / denom)

    var cur_data = [] 

